import React from "react";
import { Link } from "react-router-dom";
import { useTranslation } from "../contexts/LanguageContext";
import "../styles/Header.css";

const Header = ({ user, onLogout }) => {
  const { t } = useTranslation();
  
  return (
    <header className="header">
      <div className="header-logo">
        <Link to="/">SWE-SYS</Link>
      </div>
      
      {user && (
        <nav className="header-nav">
          <ul>
            <li><Link to="/">{t('dashboard')}</Link></li>
            <li><Link to="/teachers">{t('teachers')}</Link></li>
            <li><Link to="/schedules">{t('schedules')}</Link></li>
          </ul>
        </nav>
      )}
      
      <div className="header-actions">
        {user ? (
          <>
            <span className="username">Merhaba, {user.username}</span>
            <button onClick={onLogout} className="logout-button">{t('logout')}</button>
          </>
        ) : (
          <Link to="/login">{t('login')}</Link>
        )}
      </div>
    </header>
  );
};

export default Header;
