import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getClassrooms, deleteClassroom } from "../api";
import { FACULTIES } from "../constants/facultiesAndDepartments";
import "../styles/ClassroomList.css";

const ClassroomList = ({ token, user }) => {
  const [classrooms, setClassrooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    classroomId: null,
    classroomName: ''
  });

  useEffect(() => {
    fetchClassrooms();
  }, [token]);

  const fetchClassrooms = async () => {
    try {
      const data = await getClassrooms(token);
      setClassrooms(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching classrooms:", error);
      setError("Failed to load classrooms");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      classroomId: id,
      classroomName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      classroomId: null,
      classroomName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteClassroom(deleteConfirm.classroomId, token);
      setDeleteConfirm({
        show: false,
        classroomId: null,
        classroomName: ''
      });
      // Derslik listesini yeniden yükle
      fetchClassrooms();
    } catch (error) {
      console.error("Error deleting classroom:", error);
      setError("Failed to delete classroom. " + (error.detail || ""));
    }
  };

  // Kullanıcının admin yetkisi olup olmadığını kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="classroom-list-container">
      <div className="section-header">
        <div className="section-header-content">
          <h1>Derslikler</h1>
          <p className="section-description">
            Fakültelere ve bölümlere göre derslikler listesi
          </p>
        </div>
        {isAdmin && (
          <div className="section-actions">
            <Link to="/classrooms/new" className="add-button">
              <span className="btn-icon">+</span> Yeni Derslik Ekle
            </Link>
          </div>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Silme Onayı</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p><strong>{deleteConfirm.classroomName}</strong> isimli dersliği silmek istediğinizden emin misiniz?</p>
              <p className="warning-text">Bu işlem geri alınamaz.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">İptal</button>
              <button onClick={confirmDelete} className="btn-delete">Sil</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">Derslikler yükleniyor...</div>
      ) : classrooms.length === 0 ? (
        <div className="no-data-message">
          <div className="empty-state">
            <div className="empty-state-icon">🏫</div>
            <h3>Henüz derslik eklenmemiş</h3>
            {isAdmin && (
              <Link to="/classrooms/new" className="add-button-secondary">
                Derslik Ekle
              </Link>
            )}
          </div>
        </div>
      ) : (
        <div className="faculties-list">
          {FACULTIES.map(faculty => {
            // Fakülteye ait derslikler
            const facultyClassrooms = classrooms.filter(classroom => classroom.faculty === faculty.name);
            
            // Eğer fakültede derslik yoksa gösterme
            if (facultyClassrooms.length === 0) return null;

            return (
              <div className="faculty-card" key={faculty.id}>
                <div className="faculty-header">
                  <h2>{faculty.name}</h2>
                </div>
                <div className="departments">
                  {/* Fakülte altındaki benzersiz bölüm adlarını bul */}
                  {Array.from(new Set(facultyClassrooms.map(classroom => classroom.department)))
                    .sort()
                    .map(departmentName => {
                      // Bölüme ait derslikler
                      const departmentClassrooms = facultyClassrooms.filter(
                        classroom => classroom.department === departmentName
                      );

                      return (
                        <div className="department-card" key={departmentName}>
                          <div className="department-header">
                            <h3>{departmentName}</h3>
                            <span className="classroom-count">{departmentClassrooms.length} derslik</span>
                          </div>
                          <div className="classrooms-grid">
                            {departmentClassrooms.map(classroom => (
                              <div className="classroom-card" key={classroom.id}>
                                <h4 className="classroom-name">{classroom.name}</h4>
                                <div className="classroom-details">
                                  <div className="detail-row">
                                    <span className="detail-label">Tür:</span> 
                                    <span className="detail-value">{classroom.type}</span>
                                  </div>
                                  <div className="detail-row">
                                    <span className="detail-label">Kapasite:</span> 
                                    <span className="detail-value capacity-badge">
                                      {classroom.capacity} <span className="capacity-icon">👥</span>
                                    </span>
                                  </div>
                                </div>
                                {isAdmin && (
                                  <div className="classroom-actions">
                                    <Link to={`/classrooms/edit/${classroom.id}`} className="btn-edit">
                                      Düzenle
                                    </Link>
                                    <button 
                                      className="btn-delete" 
                                      onClick={() => handleDeleteClick(classroom.id, classroom.name)}
                                    >
                                      Sil
                                    </button>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ClassroomList;
