import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getCurrentUser } from '../api';
import { useLanguage } from '../contexts/LanguageContext';
import '../styles/Settings.css';

const Settings = ({ token }) => {
  const navigate = useNavigate();
  const { t, currentLanguage, changeLanguage, languages } = useLanguage();
  
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  
  const [settings, setSettings] = useState({
    theme: localStorage.getItem('theme') || 'light',
    language: currentLanguage,
    notifications: localStorage.getItem('notifications') === 'true' || false,
    autoSave: localStorage.getItem('autoSave') === 'true' || true,
    compactView: localStorage.getItem('compactView') === 'true' || false
  });
  
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const userData = await getCurrentUser(token);
        setUser(userData);
      } catch (err) {
        console.error('Error fetching user data:', err);
        setError('Failed to load user data. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [token]);

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    const newValue = type === 'checkbox' ? checked : value;
    
    setSettings(prev => ({
      ...prev,
      [name]: newValue
    }));
    
    // Apply theme immediately when changed
    if (name === 'theme') {
      document.documentElement.setAttribute('data-theme', value);
    }
    
    // Apply language immediately when changed
    if (name === 'language') {
      changeLanguage(value);
    }
    
    // Reset saved status when settings change
    setSaved(false);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Save settings to localStorage
    Object.entries(settings).forEach(([key, value]) => {
      localStorage.setItem(key, value.toString());
    });
    
    setSaved(true);
    
    // Reset saved notification after a delay
    setTimeout(() => {
      setSaved(false);
    }, 3000);
  };

  if (loading) return <div className="settings-loading">{t('loading')}</div>;

  return (
    <div className="settings-container">
      <div className="settings-header">
        <h1>{t('settings')}</h1>
        <p className="settings-description">
          {t('settingsDescription') || 'Customize your application preferences and appearance'}
        </p>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      {saved && <div className="success-message">{t('settingsSavedSuccess') || 'Settings saved successfully!'}</div>}
      
      <form onSubmit={handleSubmit} className="settings-form">
        <div className="settings-section">
          <h2>{t('appearance')}</h2>
          
          <div className="form-group">
            <label htmlFor="theme">{t('theme')}</label>
            <div className="theme-selector">
              <div 
                className={`theme-option ${settings.theme === 'light' ? 'selected' : ''}`}
                onClick={() => handleChange({ target: { name: 'theme', value: 'light' } })}
              >
                <div className="theme-preview light-theme">
                  <div className="preview-header"></div>
                  <div className="preview-content"></div>
                </div>
                <span>{t('light')}</span>
              </div>
              <div 
                className={`theme-option ${settings.theme === 'dark' ? 'selected' : ''}`}
                onClick={() => handleChange({ target: { name: 'theme', value: 'dark' } })}
              >
                <div className="theme-preview dark-theme">
                  <div className="preview-header"></div>
                  <div className="preview-content"></div>
                </div>
                <span>{t('dark')}</span>
              </div>
              <div 
                className={`theme-option ${settings.theme === 'system' ? 'selected' : ''}`}
                onClick={() => handleChange({ target: { name: 'theme', value: 'system' } })}
              >
                <div className="theme-preview system-theme">
                  <div className="preview-header"></div>
                  <div className="preview-content"></div>
                </div>
                <span>{t('system')}</span>
              </div>
            </div>
          </div>
          
          <div className="form-group">
            <label htmlFor="language">{t('language')}</label>
            <select
              id="language"
              name="language"
              value={settings.language}
              onChange={handleChange}
            >
              {languages.map(lang => (
                <option key={lang.code} value={lang.code}>
                  {lang.name}
                </option>
              ))}
            </select>
          </div>
          
          <div className="form-group checkbox-group">
            <input
              type="checkbox"
              id="compactView"
              name="compactView"
              checked={settings.compactView}
              onChange={handleChange}
            />
            <label htmlFor="compactView">{t('compactView')}</label>
          </div>
        </div>
        
        <div className="settings-section">
          <h2>{t('notifications')}</h2>
          
          <div className="form-group checkbox-group">
            <input
              type="checkbox"
              id="notifications"
              name="notifications"
              checked={settings.notifications}
              onChange={handleChange}
            />
            <label htmlFor="notifications">{t('enableNotifications')}</label>
          </div>
        </div>
        
        <div className="settings-section">
          <h2>{t('editor')}</h2>
          
          <div className="form-group checkbox-group">
            <input
              type="checkbox"
              id="autoSave"
              name="autoSave"
              checked={settings.autoSave}
              onChange={handleChange}
            />
            <label htmlFor="autoSave">{t('autoSave')}</label>
          </div>
        </div>
        
        <div className="settings-section">
          <h2>{t('accountInfo')}</h2>
          
          <div className="account-info">
            <div className="account-info-item">
              <span className="info-label">{t('username')}:</span>
              <span className="info-value">{user?.username || 'N/A'}</span>
            </div>
            <div className="account-info-item">
              <span className="info-label">{t('role')}:</span>
              <span className="info-value">{user?.role || 'N/A'}</span>
            </div>
          </div>
        </div>
        
        <div className="form-actions">
          <button type="button" onClick={() => navigate(-1)} className="btn-cancel">
            {t('cancel')}
          </button>
          <button type="submit" className="btn-save">
            {t('save')}
          </button>
        </div>
      </form>
    </div>
  );
};

export default Settings;
