import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getStatistics } from "../api";
import { useTranslation } from "../contexts/LanguageContext";
import "../styles/Dashboard.css";

const Dashboard = ({ token }) => {
  const { t } = useTranslation();
  const [stats, setStats] = useState({
    teacherCount: 0,
    courseCount: 0,
    classroomCount: 0,
    scheduleCount: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStats = async () => {
      if (!token) return;
      
      try {
        // API'den ger√ßek verileri al
        const data = await getStatistics(token);
        console.log("Statistics data received:", data); // Debug i√ßin log eklendi
        setStats(data);
        setLoading(false);
        setError(null);
      } catch (error) {
        console.error("Error fetching statistics:", error);
        setError(error.detail || "Failed to load statistics");
        setLoading(false);
        
        // API hatasƒ± durumunda varsayƒ±lan deƒüerler g√∂sterme
        setStats({
          teacherCount: 0,
          courseCount: 0,
          classroomCount: 0,
          scheduleCount: 0
        });
      }
    };
    
    fetchStats();
    
    // Sayfa y√ºklendiƒüinde veriyi √ßekiyoruz, sonraki istekler i√ßin temizleyici fonksiyon
    const intervalId = setInterval(fetchStats, 60000); // Her 60 saniyede bir g√ºncelleyelim
    
    return () => {
      clearInterval(intervalId); // React component unmount olduƒüunda interval'i temizle
    };
  }, [token]); // Sadece token deƒüi≈ütiƒüinde effect'i √ßalƒ±≈ütƒ±r

  return (
    <div className="dashboard">
      <h1>{t('welcome')}</h1>
      <p className="dashboard-subtitle">Eƒüitim Y√∂netimi Otomasyon Sistemi</p>
      
      {error && <div className="error-message">{error}</div>}
      
      <div className="stats-container">
        <div className="stat-card">
          <div className="stat-icon">üë•</div>
          <div className="stat-number">{loading ? '...' : stats.teacherCount}</div>
          <div className="stat-label">{t('teachers')}</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìö</div>
          <div className="stat-number">{loading ? '...' : stats.courseCount}</div>
          <div className="stat-label">{t('courses')}</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üè´</div>
          <div className="stat-number">{loading ? '...' : stats.classroomCount}</div>
          <div className="stat-label">{t('classrooms')}</div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">üìÖ</div>
          <div className="stat-number">{loading ? '...' : stats.scheduleCount}</div>
          <div className="stat-label">{t('scheduleCount')}</div>
        </div>
      </div>

      <h2>{t('quickAccess')}</h2>
      <div className="dashboard-grid">
        <div className="dashboard-card">
          <h3>üë• {t('teachers')}</h3>
          <p>√ñƒüretmen bilgilerini ve uygunluklarƒ±nƒ± y√∂netin</p>
          <div className="card-actions">
            <Link to="/teachers" className="card-btn primary">{t('teachers')}</Link>
            <Link to="/teachers/new" className="card-btn secondary">{t('add')}</Link>
          </div>
        </div>
        <div className="dashboard-card">
          <h3>üìö {t('courses')}</h3>
          <p>Ders bilgilerini g√∂r√ºnt√ºleyin ve y√∂netin</p>
          <div className="card-actions">
            <Link to="/courses" className="card-btn primary">{t('courses')}</Link>
            <Link to="/courses/new" className="card-btn secondary">{t('add')}</Link>
          </div>
        </div>
        <div className="dashboard-card">
          <h3>üè´ {t('classrooms')}</h3>
          <p>Derslik kaynaklarƒ±nƒ± g√∂r√ºnt√ºleyin ve y√∂netin</p>
          <div className="card-actions">
            <Link to="/classrooms" className="card-btn primary">{t('classrooms')}</Link>
            <Link to="/classrooms/new" className="card-btn secondary">{t('add')}</Link>
          </div>
        </div>
        <div className="dashboard-card">
          <h3>üìÖ {t('schedules')}</h3>
          <p>Ders programlarƒ±nƒ± g√∂r√ºnt√ºleyin ve y√∂netin</p>
          <div className="card-actions">
            <Link to="/schedules" className="card-btn primary">{t('schedules')}</Link>
            <Link to="/scheduler" className="card-btn secondary">{t('scheduler')}</Link>
