export const FACULTIES = [
  { id: 'engineering', name: 'Faculty of Engineering' },
  { id: 'science', name: 'Faculty of Science' },
  { id: 'arts', name: 'Faculty of Arts and Humanities' },
  { id: 'business', name: 'Faculty of Business and Economics' },
  { id: 'education', name: 'Faculty of Education' },
  { id: 'medicine', name: 'Faculty of Medicine' },
  { id: 'law', name: 'Faculty of Law' }
];

// Her fakülteye ait bölümler
export const DEPARTMENTS = {
  'engineering': [
    { id: 'ceng', name: 'Computer Engineering' },
    { id: 'ee', name: 'Electrical Engineering' },
    { id: 'me', name: 'Mechanical Engineering' },
    { id: 'ie', name: 'Industrial Engineering' },
    { id: 'ce', name: 'Civil Engineering' }
  ],
  'science': [
    { id: 'physics', name: 'Physics' },
    { id: 'chemistry', name: 'Chemistry' },
    { id: 'biology', name: 'Biology' },
    { id: 'math', name: 'Mathematics' },
    { id: 'stats', name: 'Statistics' }
  ],
  'arts': [
    { id: 'history', name: 'History' },
    { id: 'literature', name: 'Literature' },
    { id: 'philosophy', name: 'Philosophy' },
    { id: 'linguistics', name: 'Linguistics' }
  ],
  'business': [
    { id: 'business', name: 'Business Administration' },
    { id: 'economics', name: 'Economics' },
    { id: 'accounting', name: 'Accounting and Finance' },
    { id: 'marketing', name: 'Marketing' }
  ],
  'education': [
    { id: 'elemEd', name: 'Elementary Education' },
    { id: 'secEd', name: 'Secondary Education' },
    { id: 'specialEd', name: 'Special Education' }
  ],
  'medicine': [
    { id: 'medicine', name: 'Medicine' },
    { id: 'nursing', name: 'Nursing' },
    { id: 'pharmacy', name: 'Pharmacy' }
  ],
  'law': [
    { id: 'law', name: 'Law' },
    { id: 'intLaw', name: 'International Law' }
  ]
};

// Fakülte ID'sine göre bölüm listesini al
export const getDepartmentsByFaculty = (facultyId) => {
  return DEPARTMENTS[facultyId] || [];
};
