import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getCourses, deleteCourse } from "../api";
import { FACULTIES } from "../constants/facultiesAndDepartments";
import "../styles/CourseList.css";

const CourseList = ({ token, user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    courseId: null,
    courseName: ''
  });

  useEffect(() => {
    fetchCourses();
  }, [token]);

  const fetchCourses = async () => {
    try {
      const data = await getCourses(token);
      setCourses(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching courses:", error);
      setError("Failed to load courses");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      courseId: id,
      courseName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      courseId: null,
      courseName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteCourse(deleteConfirm.courseId, token);
      setDeleteConfirm({
        show: false,
        courseId: null,
        courseName: ''
      });
      // Ders listesini yeniden yÃ¼kle
      fetchCourses();
    } catch (error) {
      console.error("Error deleting course:", error);
      setError("Failed to delete course. " + (error.detail || ""));
    }
  };

  // KullanÄ±cÄ±nÄ±n admin yetkisi olup olmadÄ±ÄŸÄ±nÄ± kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="course-list-container">
      <div className="section-header">
        <div className="section-header-content">
          <h1>Dersler</h1>
          <p className="section-description">
            FakÃ¼ltelere ve bÃ¶lÃ¼mlere gÃ¶re dersler listesi
          </p>
        </div>
        {isAdmin && (
          <div className="section-actions">
            <Link to="/courses/new" className="add-button">
              <span className="btn-icon">+</span> Yeni Ders Ekle
            </Link>
          </div>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Silme OnayÄ±</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p><strong>{deleteConfirm.courseName}</strong> isimli dersi silmek istediÄŸinizden emin misiniz?</p>
              <p className="warning-text">Bu iÅŸlem geri alÄ±namaz.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">Ä°ptal</button>
              <button onClick={confirmDelete} className="btn-delete">Sil</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">Dersler yÃ¼kleniyor...</div>
      ) : courses.length === 0 ? (
        <div className="no-data-message">
          <div className="empty-state">
            <div className="empty-state-icon">ðŸ“š</div>
            <h3>HenÃ¼z ders eklenmemiÅŸ</h3>
            {isAdmin && (
              <Link to="/courses/new" className="add-button-secondary">
                Ders Ekle
              </Link>
            )}
          </div>
        </div>
      ) : (
        <div className="faculties-list">
          {FACULTIES.map(faculty => {
            // FakÃ¼lteye ait dersler
            const facultyCourses = courses.filter(course => course.faculty === faculty.name);
            
            // EÄŸer fakÃ¼ltede ders yoksa gÃ¶sterme
            if (facultyCourses.length === 0) return null;

            return (
              <div className="faculty-card" key={faculty.id}>
                <div className="faculty-header">
                  <h2>{faculty.name}</h2>
                </div>
                <div className="departments">
                  {/* FakÃ¼lte altÄ±ndaki benzersiz bÃ¶lÃ¼m adlarÄ±nÄ± bul */}
                  {Array.from(new Set(facultyCourses.map(course => course.department)))
                    .sort()
                    .map(departmentName => {
                      // BÃ¶lÃ¼me ait dersler
                      const departmentCourses = facultyCourses.filter(
                        course => course.department === departmentName
                      );

                      return (
                        <div className="department-card" key={departmentName}>
                          <div className="department-header">
                            <h3>{departmentName}</h3>
                            <span className="course-count">{departmentCourses.length} ders</span>
                          </div>
                          <div className="courses-grid">
                            {departmentCourses.map(course => (
                              <div 
                                className={`course-card ${course.is_active ? 'active-course' : 'inactive-course'}`} 
                                key={course.id}
                              >
                                <div className="course-header">
                                  <div className="course-code">{course.code}</div>
                                  {!course.is_active && <span className="inactive-badge">Pasif</span>}
                                </div>
                                <h4 className="course-name">{course.name}</h4>
                                <div className="course-details">
                                  <div className="detail-row">
                                    <span className="detail-label">Ã–ÄŸretmen:</span> 
                                    <span className="detail-value">
                                      {course.teacher ? course.teacher.name : 'AtanmamÄ±ÅŸ'}
                                    </span>
                                  </div>
                                  <div className="detail-row">
                                    <span className="detail-label">Seviye:</span> 
                                    <span className="detail-value">{course.level}</span>
                                  </div>
                                  <div className="detail-row">
                                    <span className="detail-label">AKTS:</span> 
                                    <span className="detail-value">{course.ects}</span>
                                  </div>
                                  <div className="detail-row">
                                    <span className="detail-label">Ã–ÄŸrenci:</span> 
                                    <span className="detail-value">{course.student_count || 0}</span>
                                  </div>
                                </div>
                                {isAdmin && (
                                  <div className="course-actions">
                                    <Link to={`/courses/edit/${course.id}`} className="btn-edit">
                                      DÃ¼zenle
                                    </Link>
                                    <button 
                                      className="btn-delete" 
                                      onClick={() => handleDeleteClick(course.id, course.name)}
                                    >
                                      Sil
                                    </button>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default CourseList;
