import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getCourses, deleteCourse } from "../api";
import "../styles/CourseList.css";

const CourseList = ({ token, user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    courseId: null,
    courseName: ''
  });
  const [groupedCourses, setGroupedCourses] = useState({});
  const [facultyList, setFacultyList] = useState([]);

  useEffect(() => {
    fetchCourses();
  }, [token]);

  const fetchCourses = async () => {
    try {
      const data = await getCourses(token);
      setCourses(data);

      // Fakülte ve bölümlere göre gruplayarak organize edelim
      const grouped = {};
      const faculties = new Set();

      data.forEach(course => {
        // Fakülteyi kaydedelim
        faculties.add(course.faculty);

        // Fakülte bazında grupla
        if (!grouped[course.faculty]) {
          grouped[course.faculty] = {};
        }

        // Bölüm bazında grupla
        if (!grouped[course.faculty][course.department]) {
          grouped[course.faculty][course.department] = [];
        }

        // Dersi ilgili fakülte ve bölüme ekle
        grouped[course.faculty][course.department].push(course);
      });

      setGroupedCourses(grouped);
      setFacultyList([...faculties].sort());
      setLoading(false);
    } catch (error) {
      console.error("Error fetching courses:", error);
      setError("Failed to load courses");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      courseId: id,
      courseName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      courseId: null,
      courseName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteCourse(deleteConfirm.courseId, token);
      setDeleteConfirm({
        show: false,
        courseId: null,
        courseName: ''
      });
      // Ders listesini yeniden yükle
      fetchCourses();
    } catch (error) {
      console.error("Error deleting course:", error);
      setError("Failed to delete course. " + (error.detail || ""));
    }
  };

  // Kullanıcının admin yetkisi olup olmadığını kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="course-list-container">
      <div className="course-header">
        <h1>Courses</h1>
        {isAdmin && (
          <Link to="/courses/new" className="add-course-btn">
            <span className="btn-icon">+</span> Add New Course
          </Link>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Delete Confirmation</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to delete <strong>{deleteConfirm.courseName}</strong>?</p>
              <p className="warning-text">This action cannot be undone.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">Cancel</button>
              <button onClick={confirmDelete} className="btn-delete">Delete</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">Loading courses...</div>
      ) : courses.length === 0 ? (
        <div className="no-courses">No courses found. Please add some courses.</div>
      ) : (
        <div className="courses-by-faculty">
          {facultyList.map(faculty => (
            <div className="faculty-section" key={faculty}>
              <h2 className="faculty-name">{faculty}</h2>
              
              {Object.keys(groupedCourses[faculty]).map(department => (
                <div className="department-section" key={department}>
                  <h3 className="department-name">{department}</h3>
                  
                  <table className="course-table">
                    <thead>
                      <tr>
                        <th>Code</th>
                        <th>Name</th>
                        <th>Teacher</th>
                        <th>Level</th>
                        <th>ECTS</th>
                        <th>Students</th>
                        <th>Status</th>
                        {isAdmin && <th>Actions</th>}
                      </tr>
                    </thead>
                    <tbody>
                      {groupedCourses[faculty][department].map(course => (
                        <tr key={course.id}>
                          <td className="course-code">{course.code}</td>
                          <td>{course.name}</td>
                          <td>{course.teacher ? course.teacher.name : (course.teacher_id ? 'Loading...' : 'No teacher assigned')}</td>
                          <td>{course.level}</td>
                          <td className="text-center">{course.ects}</td>
                          <td className="text-center">{course.student_count}</td>
                          <td>
                            <span className={`status-badge ${course.is_active ? 'active' : 'inactive'}`}>
                              {course.is_active ? 'Active' : 'Inactive'}
                            </span>
                          </td>
                          {isAdmin && (
                            <td className="actions-cell">
                              <Link 
                                to={`/courses/edit/${course.id}`} 
                                className="btn-edit"
                              >
                                Edit
                              </Link>
                              <button 
                                className="btn-delete" 
                                onClick={() => handleDeleteClick(course.id, course.name)}
                              >
                                Delete
                              </button>
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CourseList;
