import React, { useState } from "react";
import { login } from "../api";
import "../styles/Login.css";
import logo from "../assets/logo.png"; // Gerektiğinde bir logo ekleyin

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError("");
    
    try {
      const data = await login(username, password);
      onLogin(data.access_token);
    } catch (err) {
      console.error("Login error:", err);
      setError(err.detail || "Invalid username or password");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img src={logo} alt="SWE-SYS Logo" className="login-logo" />
          <h2>SWE-SYS Login</h2>
          <p className="login-subtitle">Eğitim Yönetiminde Yeni Nesil Otomasyon</p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Username</label>
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              autoFocus
              placeholder="Enter your username"
            />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              placeholder="Enter your password"
            />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button type="submit" disabled={isLoading} className="login-button">
            {isLoading ? (
              <span className="loading-spinner"></span>
            ) : (
              "Login"
            )}
          </button>
        </form>
        <div className="login-info">
          <p>Demo Credentials:</p>
          <div className="demo-credentials">
            <div className="credential">
              <span>Admin:</span>
              <code>admin / admin123</code>
            </div>
            <div className="credential">
              <span>Teacher:</span>
              <code>teacher / teacher123</code>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
