import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getClassrooms, deleteClassroom } from "../api";
import "../styles/ClassroomList.css";

const ClassroomList = ({ token, user }) => {
  const [classrooms, setClassrooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    classroomId: null,
    classroomName: ''
  });
  const [groupedClassrooms, setGroupedClassrooms] = useState({});
  const [facultyList, setFacultyList] = useState([]);

  useEffect(() => {
    fetchClassrooms();
  }, [token]);

  const fetchClassrooms = async () => {
    try {
      const data = await getClassrooms(token);
      setClassrooms(data);

      // Fakülte ve bölümlere göre gruplayarak organize edelim
      const grouped = {};
      const faculties = new Set();

      data.forEach(classroom => {
        // Fakülteyi kaydedelim
        faculties.add(classroom.faculty);

        // Fakülte bazında grupla
        if (!grouped[classroom.faculty]) {
          grouped[classroom.faculty] = {};
        }

        // Bölüm bazında grupla
        if (!grouped[classroom.faculty][classroom.department]) {
          grouped[classroom.faculty][classroom.department] = [];
        }

        // Sınıfı ilgili fakülte ve bölüme ekle
        grouped[classroom.faculty][classroom.department].push(classroom);
      });

      setGroupedClassrooms(grouped);
      setFacultyList([...faculties].sort());
      setLoading(false);
    } catch (error) {
      console.error("Error fetching classrooms:", error);
      setError("Failed to load classrooms");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      classroomId: id,
      classroomName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      classroomId: null,
      classroomName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteClassroom(deleteConfirm.classroomId, token);
      setDeleteConfirm({
        show: false,
        classroomId: null,
        classroomName: ''
      });
      // Derslik listesini yeniden yükle
      fetchClassrooms();
    } catch (error) {
      console.error("Error deleting classroom:", error);
      setError("Failed to delete classroom. " + (error.detail || ""));
    }
  };

  // Kullanıcının admin yetkisi olup olmadığını kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="classroom-list-container">
      <div className="classroom-header">
        <h1>Classrooms</h1>
        {isAdmin && (
          <Link to="/classrooms/new" className="add-classroom-btn">
            <span className="btn-icon">+</span> Add New Classroom
          </Link>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Delete Confirmation</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to delete <strong>{deleteConfirm.classroomName}</strong>?</p>
              <p className="warning-text">This action cannot be undone.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">Cancel</button>
              <button onClick={confirmDelete} className="btn-delete">Delete</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">Loading classrooms...</div>
      ) : classrooms.length === 0 ? (
        <div className="no-classrooms">No classrooms found. Please add some classrooms.</div>
      ) : (
        <div className="classrooms-by-faculty">
          {facultyList.map(faculty => (
            <div className="faculty-section" key={faculty}>
              <h2 className="faculty-name">{faculty}</h2>
              
              {Object.keys(groupedClassrooms[faculty]).map(department => (
                <div className="department-section" key={department}>
                  <h3 className="department-name">{department}</h3>
                  
                  <table className="classroom-table">
                    <thead>
                      <tr>
                        <th>Name</th>
                        <th>Type</th>
                        <th>Capacity</th>
                        {isAdmin && <th>Actions</th>}
                      </tr>
                    </thead>
                    <tbody>
                      {groupedClassrooms[faculty][department].map(classroom => (
                        <tr key={classroom.id}>
                          <td className="classroom-name">{classroom.name}</td>
                          <td>{classroom.type}</td>
                          <td className="text-center">{classroom.capacity}</td>
                          {isAdmin && (
                            <td className="actions-cell">
                              <Link 
                                to={`/classrooms/edit/${classroom.id}`} 
                                className="btn-edit"
                              >
                                Edit
                              </Link>
                              <button 
                                className="btn-delete" 
                                onClick={() => handleDeleteClick(classroom.id, classroom.name)}
                              >
                                Delete
                              </button>
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              ))}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ClassroomList;
