import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getTeachers, deleteTeacher } from "../api";
import { FACULTIES } from "../constants/facultiesAndDepartments";
import "../styles/TeacherList.css";

const TeacherList = ({ token, user }) => {
  const [teachers, setTeachers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    teacherId: null,
    teacherName: ''
  });

  useEffect(() => {
    fetchTeachers();
  }, [token]);

  const fetchTeachers = async () => {
    try {
      const data = await getTeachers(token);
      setTeachers(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching teachers:", error);
      setError("Failed to load teachers");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      teacherId: id,
      teacherName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      teacherId: null,
      teacherName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteTeacher(deleteConfirm.teacherId, token);
      setDeleteConfirm({
        show: false,
        teacherId: null,
        teacherName: ''
      });
      // √ñƒüretmen listesini yeniden y√ºkle
      fetchTeachers();
    } catch (error) {
      console.error("Error deleting teacher:", error);
      setError("Failed to delete teacher. " + (error.detail || ""));
    }
  };

  // Kullanƒ±cƒ±nƒ±n admin yetkisi olup olmadƒ±ƒüƒ±nƒ± kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="teacher-list-container">
      <div className="section-header">
        <div className="section-header-content">
          <h1>√ñƒüretim G√∂revlileri</h1>
          <p className="section-description">
            Fak√ºltelere ve b√∂l√ºmlere g√∂re √∂ƒüretim g√∂revlileri listesi
          </p>
        </div>
        {isAdmin && (
          <div className="section-actions">
            <Link to="/teachers/new" className="add-button">
              <span className="btn-icon">+</span> Yeni √ñƒüretim G√∂revlisi Ekle
            </Link>
          </div>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Silme Onayƒ±</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p><strong>{deleteConfirm.teacherName}</strong> isimli √∂ƒüretim g√∂revlisini silmek istediƒüinizden emin misiniz?</p>
              <p className="warning-text">Bu i≈ülem geri alƒ±namaz.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">ƒ∞ptal</button>
              <button onClick={confirmDelete} className="btn-delete">Sil</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">√ñƒüretim g√∂revlileri y√ºkleniyor...</div>
      ) : teachers.length === 0 ? (
        <div className="no-data-message">
          <div className="empty-state">
            <div className="empty-state-icon">üë®‚Äçüè´</div>
            <h3>Hen√ºz √∂ƒüretim g√∂revlisi eklenmemi≈ü</h3>
            {isAdmin && (
              <Link to="/teachers/new" className="add-button-secondary">
                √ñƒüretim G√∂revlisi Ekle
              </Link>
            )}
          </div>
        </div>
      ) : (
        <div className="faculties-list">
          {FACULTIES.map(faculty => {
            // Fak√ºlteye ait √∂ƒüretim g√∂revlileri
            const facultyTeachers = teachers.filter(teacher => teacher.faculty === faculty.name);
            
            // Eƒüer fak√ºltede √∂ƒüretim g√∂revlisi yoksa g√∂sterme
            if (facultyTeachers.length === 0) return null;

            return (
              <div className="faculty-card" key={faculty.id}>
                <div className="faculty-header">
                  <h2>{faculty.name}</h2>
                </div>
                <div className="departments">
                  {/* Fak√ºlte altƒ±ndaki benzersiz b√∂l√ºm adlarƒ±nƒ± bul */}
                  {Array.from(new Set(facultyTeachers.map(teacher => teacher.department)))
                    .sort()
                    .map(departmentName => {
                      // B√∂l√ºme ait √∂ƒüretim g√∂revlileri
                      const departmentTeachers = facultyTeachers.filter(
                        teacher => teacher.department === departmentName
                      );

                      return (
                        <div className="department-card" key={departmentName}>
                          <div className="department-header">
                            <h3>{departmentName}</h3>
                          </div>
                          <div className="teacher-list">
                            {departmentTeachers.map(teacher => (
                              <div className="teacher-item" key={teacher.id}>
                                <div className="teacher-info">
                                  <div className="teacher-name">{teacher.name}</div>
                                  <div className="teacher-email">{teacher.email}</div>
                                  <div className="teacher-details">
                                    <span className="detail-label">√áalƒ±≈üma G√ºnleri:</span> {teacher.working_days}
                                  </div>
                                  <div className="teacher-details">
                                    <span className="detail-label">√áalƒ±≈üma Saatleri:</span> {teacher.working_hours}
                                  </div>
                                </div>
                                {isAdmin && (
                                  <div className="teacher-actions">
                                    <Link to={`/teachers/edit/${teacher.id}`} className="btn-edit">
                                      D√ºzenle
                                    </Link>
                                    <button 
                                      className="btn-delete" 
                                      onClick={() => handleDeleteClick(teacher.id, teacher.name)}
                                    >
                                      Sil
                                    </button>
                                  </div>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      );
                    })}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default TeacherList;
