import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getCourses, deleteCourse } from "../api";
import "../styles/CourseList.css";
import { FACULTIES } from '../constants/facultiesAndDepartments';

const CourseList = ({ token, user }) => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState({
    show: false,
    courseId: null,
    courseName: ''
  });
  const [groupedCourses, setGroupedCourses] = useState({});
  const [facultyList, setFacultyList] = useState([]);

  useEffect(() => {
    fetchCourses();
  }, [token]);

  const fetchCourses = async () => {
    try {
      const data = await getCourses(token);
      setCourses(data);

      // Fakülte ve bölümlere göre gruplayarak organize edelim
      const grouped = {};
      const faculties = new Set();

      data.forEach(course => {
        // Fakülteyi kaydedelim
        faculties.add(course.faculty);

        // Fakülte bazında grupla
        if (!grouped[course.faculty]) {
          grouped[course.faculty] = {};
        }

        // Bölüm bazında grupla
        if (!grouped[course.faculty][course.department]) {
          grouped[course.faculty][course.department] = [];
        }

        // Dersi ilgili fakülte ve bölüme ekle
        grouped[course.faculty][course.department].push(course);
      });

      setGroupedCourses(grouped);
      setFacultyList([...faculties].sort());
      setLoading(false);
    } catch (error) {
      console.error("Error fetching courses:", error);
      setError("Failed to load courses");
      setLoading(false);
    }
  };

  const handleDeleteClick = (id, name) => {
    setDeleteConfirm({
      show: true,
      courseId: id,
      courseName: name
    });
  };

  const cancelDelete = () => {
    setDeleteConfirm({
      show: false,
      courseId: null,
      courseName: ''
    });
  };

  const confirmDelete = async () => {
    try {
      await deleteCourse(deleteConfirm.courseId, token);
      setDeleteConfirm({
        show: false,
        courseId: null,
        courseName: ''
      });
      // Ders listesini yeniden yükle
      fetchCourses();
    } catch (error) {
      console.error("Error deleting course:", error);
      setError("Failed to delete course. " + (error.detail || ""));
    }
  };

  // Kullanıcının admin yetkisi olup olmadığını kontrol et
  const isAdmin = user?.role === "admin" || user?.permissions?.includes("admin");

  return (
    <div className="page-container">
      <div className="page-header">
        <h1>Courses</h1>
        {isAdmin && (
          <Link to="/courses/new" className="add-button">
            <span className="btn-icon">+</span> Add New Course
          </Link>
        )}
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {deleteConfirm.show && (
        <div className="modal-backdrop">
          <div className="delete-confirmation-modal">
            <div className="modal-header">
              <h3>Delete Confirmation</h3>
              <button className="close-button" onClick={cancelDelete}>&times;</button>
            </div>
            <div className="modal-body">
              <p>Are you sure you want to delete <strong>{deleteConfirm.courseName}</strong>?</p>
              <p className="warning-text">This action cannot be undone.</p>
            </div>
            <div className="modal-footer">
              <button onClick={cancelDelete} className="btn-cancel">Cancel</button>
              <button onClick={confirmDelete} className="btn-delete">Delete</button>
            </div>
          </div>
        </div>
      )}
      
      {loading ? (
        <div className="loading">Loading courses...</div>
      ) : courses.length === 0 ? (
        <div className="no-data-message">No courses found. Please add some courses.</div>
      ) : (
        <div className="faculty-grid">
          {facultyList.map(faculty => (
            <div className="faculty-card" key={faculty}>
              <div className="faculty-header">
                <h2 className="faculty-title">{faculty}</h2>
                <div className="faculty-stats">
                  <span className="stat-item">
                    {Object.values(groupedCourses[faculty]).reduce((sum, dept) => sum + dept.length, 0)} Courses
                  </span>
                </div>
              </div>
              
              <div className="department-list">
                {Object.keys(groupedCourses[faculty]).map(department => (
                  <div className="department-item" key={department}>
                    <div className="department-header" onClick={() => window.location.href = `/faculties/${FACULTIES.find(f => f.name === faculty)?.id}/departments/${department.toLowerCase().replace(/\s+/g, '-')}`}>
                      <h3 className="department-name">{department}</h3>
                      <div className="department-stats">
                        <span className="course-count">
                          {groupedCourses[faculty][department].length} Courses
                        </span>
                      </div>
                    </div>
                    
                    <div className="course-list">
                      {groupedCourses[faculty][department].map(course => (
                        <div className="course-item" key={course.id}>
                          <div className="course-details">
                            <div className="course-code-name">
                              <span className="course-code">{course.code}</span>
                              <span className="course-name">{course.name}</span>
                            </div>
                            <div className="course-meta">
                              <span className="teacher-name">
                                {course.teacher ? course.teacher.name : 'No teacher assigned'}
                              </span>
                              <span className={`status-badge ${course.is_active ? 'active' : 'inactive'}`}>
                                {course.is_active ? 'Active' : 'Inactive'}
                              </span>
                            </div>
                          </div>
                          
                          {isAdmin && (
                            <div className="course-actions">
                              <Link to={`/courses/edit/${course.id}`} className="btn-edit">Edit</Link>
                              <button className="btn-delete" onClick={() => handleDeleteClick(course.id, course.name)}>Delete</button>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default CourseList;
