import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { createTeacher } from '../api';
import '../styles/TeacherForm.css';

const TeacherForm = ({ token }) => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    faculty: '',
    department: '',
    working_days: '',
    working_hours: ''
  });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      await createTeacher(formData, token);
      navigate('/teachers');
    } catch (err) {
      console.error('Error creating teacher:', err);
      setError(err.detail || 'Failed to create teacher. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="teacher-form-container">
      <h2>Add New Teacher</h2>
      {error && <div className="error-message">{error}</div>}
      
      <form onSubmit={handleSubmit} className="teacher-form">
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Enter teacher's full name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email Address</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            placeholder="Enter teacher's email address"
          />
        </div>

        <div className="form-group">
          <label htmlFor="faculty">Faculty</label>
          <input
            type="text"
            id="faculty"
            name="faculty"
            value={formData.faculty}
            onChange={handleChange}
            required
            placeholder="Enter faculty name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="department">Department</label>
          <input
            type="text"
            id="department"
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
            placeholder="Enter department name"
          />
        </div>

        <div className="form-group">
          <label htmlFor="working_days">Working Days</label>
          <input
            type="text"
            id="working_days"
            name="working_days"
            value={formData.working_days}
            onChange={handleChange}
            required
            placeholder="E.g., Mon-Fri"
          />
        </div>

        <div className="form-group">
          <label htmlFor="working_hours">Working Hours</label>
          <input
            type="text"
            id="working_hours"
            name="working_hours"
            value={formData.working_hours}
            onChange={handleChange}
            required
            placeholder="E.g., 9:00-17:00"
          />
        </div>

        <div className="form-actions">
          <button type="button" onClick={() => navigate('/teachers')} className="btn-cancel">
            Cancel
          </button>
          <button type="submit" className="btn-submit" disabled={loading}>
            {loading ? 'Adding...' : 'Add Teacher'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default TeacherForm;
