import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getTeachers } from "../api";
import "../styles/TeacherList.css";

const TeacherList = ({ token }) => {
  const [teachers, setTeachers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTeachers = async () => {
      try {
        const data = await getTeachers(token);
        setTeachers(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching teachers:", error);
        setError("Failed to load teachers");
        setLoading(false);
      }
    };

    fetchTeachers();
  }, [token]);

  return (
    <div className="teacher-list-container">
      <div className="teacher-header">
        <h1>Teachers</h1>
        <Link to="/teachers/new" className="add-teacher-btn">
          <span className="btn-icon">+</span> Add New Teacher
        </Link>
      </div>
      
      {error && <div className="error-message">{error}</div>}
      
      {loading ? (
        <div className="loading">Loading teachers...</div>
      ) : (
        <table className="teacher-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Faculty</th>
              <th>Department</th>
              <th>Working Days</th>
              <th>Working Hours</th>
            </tr>
          </thead>
          <tbody>
            {teachers.length === 0 ? (
              <tr>
                <td colSpan="6" className="no-data">No teachers found</td>
              </tr>
            ) : (
              teachers.map(teacher => (
                <tr key={teacher.id}>
                  <td>{teacher.name}</td>
                  <td>{teacher.email}</td>
                  <td>{teacher.faculty}</td>
                  <td>{teacher.department}</td>
                  <td>{teacher.working_days}</td>
                  <td>{teacher.working_hours}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TeacherList;
